} else {
    let zoneHomePosition = global.get("homeassistant").homeAssistant.states["zone.home"].attributes;
    let lat1 = zoneHomePosition.latitude;
    let lon1 = zoneHomePosition.longitude;
    let lat2 = msg.position.attributes.latitude;
    let lon2 = msg.position.attributes.longitude;

    msg.payload = calculateDistance(lat1, lon1, lat2, lon2);
    return msg;

    function calculateDistance(lat1, lon1, lat2, lon2) {
        // Earth's radius, in meters
        var R = 6371e3;

        // convert latitude and longitude from degrees to radians
        lat1 = lat1 * (Math.PI / 180);
        lon1 = lon1 * (Math.PI / 180);
        lat2 = lat2 * (Math.PI / 180);
        lon2 = lon2 * (Math.PI / 180);

        // calculate the differences between the coordinates
        var dlat = lat2 - lat1;
        var dlon = lon2 - lon1;

        // calculate the distance using the Haversine formula
        var a = Math.pow(Math.sin(dlat / 2), 2) + Math.cos(lat1) * Math.cos(lat2) * Math.pow(Math.sin(dlon / 2), 2);
        var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        var d = R * c;

        // return the distance in meters
        return Math.round(d);
    }
}

