let linked_device = msg.linked_device
let entity_id = msg.entity_id
let new_state = msg.new_state
let old_state = msg.old_state
let remote = msg.remote
let entity_msgs = []
let msgs = []

if (typeof remote.decrease === "string" && typeof remote.increase === "string" && typeof msg.delay === "number") {
    let diff = new_state - old_state
    msgs.push(
        {
            payload: {
                data: {
                    entity_id: linked_device,
                    command: (diff < 0 ? remote.decrease : remote.increase),
                    num_repeats: Math.abs(diff),
                    delay_secs: msg.delay
                }
            }
        }
    )
    return [msgs, null]
} else if (remote[new_state] !== undefined) {
    for (let i = 0, c = remote[new_state]; i < c.length; i++) {
        if (typeof c[i].command === "string" && typeof c[i].delay === "number") {
            msgs.push(
                {
                    payload: {
                        data: {
                            entity_id: linked_device,
                            command: c[i].command,
                            num_repeats: 1,
                            hold_secs: 0
                        }
                    },
                    delay: c[i].delay * 1000
                }
            )
        }
    }
    if (msg.turn_off_after_command) {
        entity_msgs.push({ entity_id: entity_id, state: "off" })
    }
    return [msgs, entity_msgs]
}

let light_control = global.get("light_control") ?? {}
let brightness_pct = [100, 1]
let effects = msg.effects
effects.time = effects.time / 2
let msgs = []


// NE PAS FAIRE DE NOTIF QUAND LA LAMPE EST ETEINTE ET BLOQUEE

msgs.push({ reset: "reset" })
for (let light of msg.linked_lights) {
    let is_locked = (light_control.locked ?? []).includes(light.entity_id)
    if (!is_locked || is_locked && light.state === "on") {
        if (light.attributes.flowing !== undefined) {
            let delay = 0
            if (light.state === "off" || light.attributes.flowing === true) {
                msgs.push({ domain: "light", service: "turn_on", payload: { data: { entity_id: light.entity_id, transition: effects.time } } })
                delay = effects.time
            }
            if ((light.attributes.supported_color_modes ?? []).find(s => ["hs", "rgb", "xy"].includes(s)) && effects.saturation !== 0) {
                msgs.push({
                    delay: delay,
                    domain: "yeelight", service: "start_flow", payload: {
                        data: {
                            entity_id: light.entity_id, count: effects.count, action: "recover",
                            transitions: [
                                { "HSVTransition": [effects.color, effects.saturation, effects.time, brightness_pct[0]] },
                                { "HSVTransition": [effects.color, effects.saturation, effects.time, brightness_pct[1]] }
                            ]
                        }
                    }
                })
            } else if ((light.attributes.supported_color_modes ?? []).find(s => ["color_temp"].includes(s))) {
                msgs.push({
                    delay: delay,
                    domain: "yeelight", service: "start_flow", payload: {
                        data: {
                            entity_id: light.entity_id, count: effects.count, action: "recover",
                            transitions: [
                                { "TemperatureTransition": [light.attributes.max_color_temp_kelvin, effects.time, brightness_pct[0]] },
                                { "TemperatureTransition": [light.attributes.min_color_temp_kelvin, effects.time, brightness_pct[1]] }
                            ]
                        }
                    }
                })
            }
        } else {
            for (let i = 0; i < effects.count * 2; i++) {
                let support = (light.attributes.supported_color_modes ?? []).find(s => ["hs", "rgb", "xy"].includes(s))
                let parameters = { [support && effects.saturation !== 0 ? "hs_color" : "color_temp_kelvin"]: support && effects.saturation !== 0 ? [effects.color, effects.saturation] : i % 2 === 0 ? light.attributes.max_color_temp_kelvin : light.attributes.min_color_temp_kelvin }
                msgs.push({
                    delay: effects.time * i,
                    domain: "light",
                    service: "turn_on",
                    payload: { data: { entity_id: light.entity_id, brightness_pct: brightness_pct[i % 2], transition: effects.time, ...parameters } }
                })
            }
        }
    }
}

if (msgs.length > 1) {
    msgs.push({ flow: "stop", delay: effects.count * effects.time * 2, payload: { data: { transition: 0.5 } } })
    return [msgs]
}

if (
    Array.isArray(msg.linked_lights) && msg.linked_lights.length > 0 &&
    msg.effects !== undefined &&
    typeof msg.effects.count === "number" &&
    typeof msg.effects.color === "number" &&
    typeof msg.effects.saturation === "number" &&
    typeof msg.effects.time === "number"
) {
    return msg
}

let persons = msg.persons
