if (msg.error === true) {
    persons = msg.persons.filter(p => (p.notify_mobile_app_on_errors ?? false))
} else if (Array.isArray(msg.send_to) && msg.send_to.length !== 0) {
    persons = msg.persons.filter(p => msg.send_to.includes(p.name))
}

for (let p of persons.filter(p => p.mobile_app_name !== "")) {
