    }

    notification.payload.data["data"] = { actions: [{ action: "done", title: "Trait√©" }] }

    if (msg.data !== undefined && msg.data.actions !== undefined) {
        notification.payload.data["data"].actions = [...msg.data.actions, ...notification.payload.data["data"].actions]
    }

    notification.payload.data["data"]["tag"] = msg.notification_id
    msgs.push(notification)
}

return [msgs]

if (msg.send_mobile === true || msg.dismiss === true) {
