function set_msgs(entity_id, audio_type, content) {

    let home_configuration = global.get("home_configuration") ?? {}
    let tts = home_configuration.tts ?? ""
    let message = {
        entity_id: entity_id,
        volume_level: msg.volume_level,
        rate: (audio_type === "speak" ? 125 * content.length : 10 * 1000),
        effects: msg.effects
    }

    if (audio_type === "speak" && tts.voice !== "") {
        msgs.push({
            ...message,
            domain: "tts",
            service: tts.voice,
            payload: {
                data: {
                    entity_id: entity_id,
                    message: content,
                    options: tts.options ?? {},
                    cache: true
                }
            }
        })
    } else if (audio_type === "sound") {
        msgs.push({
            ...message,
            domain: "media_player",
            service: "play_media",
            payload: {
                data: {
                    entity_id: entity_id,
                    media_content_id: "media-source://media_source/media/" + content,
                    media_content_type: "music"
                }
            }
        })
    } else if (audio_type === "link") {
        msgs.push({
            ...message,
            domain: "media_player",
            service: "play_media",
            payload: {
                data: {
                    entity_id: entity_id,
                    media_content_id: content,
                    media_content_type: "music"
                }
            }
        })
    }
}

