const [domain] = msg.entity_id.split(".")
msg.domain = domain
msg.state = msg.state ?? ""

const serviceMap = {
    "input_boolean": state => ["turn_" + state, { entity_id: msg.entity_id }],
    "switch": state => ["turn_" + state, { entity_id: msg.entity_id }],
    "light": state => ["turn_" + state, { entity_id: msg.entity_id }],
    "input_select": state => ["select_option", { entity_id: msg.entity_id, option: state }],
    "select": state => ["select_option", { entity_id: msg.entity_id, option: state }],
    "input_number": state => ["set_value", { entity_id: msg.entity_id, value: state }],
    "input_text": state => ["set_value", { entity_id: msg.entity_id, value: state }],
    "alarm_control_panel": state => {
        const map = {
            "armed_custom_bypass": "alarm_arm_custom_bypass",
            "armed_vacation": "alarm_arm_vacation",
            "armed_night": "alarm_arm_night",
            "armed_away": "alarm_arm_away",
            "armed_home": "alarm_arm_home",
            "disarmed": "alarm_disarm",
            "triggered": "alarm_trigger"
        }
        return [map[state], { entity_id: msg.entity_id }]
    },
    "input_datetime": state => {
        let type = state.match(/^\d{4}-\d{2}-\d{2} \d{2}:\d{2}/) ? "datetime" :
            state.match(/^\d{4}-\d{2}-\d{2}/) ? "date" :
                state.match(/^\d{2}:\d{2}/) ? "time" : ""
        return type ? ["set_datetime", { entity_id: msg.entity_id, [type]: state }] : []
    },
    "execute": state => [state, null],
    "emulate": state => [null, {
        event: "emulate",
        data: {
            entity_id: msg.entity_id.replace(domain + ".", ""),
            ...state
        }
    }]
}

if (domain in serviceMap) {
    const [service, data] = serviceMap[domain](msg.state)
    if (service || data) {
        msg.service = service
        msg.payload = data ? { data } : {}
        return service ? [msg, null] : [null, msg]
    }
}

if (msg.error?.message) {
    let notAllowedError = msg.blockedErrors.find(e => (msg.error.message.includes(e)))
    if (!notAllowedError) {
        let notifications = global.get("notifications") ?? {}

