    msg.call_service = { entity_id: "input_text.json_exterior_average_temp", state: JSON.stringify(average) }
    return msg
}

function calculateAverage(average) {
    return Math.round(((average[0] * average[1] + temp_hum["exterior"]["temperature"].value) / (average[1] + 1)) * 100) / 100
}

