    if (typeof p.attributes.latitude === "number" && typeof p.attributes.longitude === "number") {
        let lat1 = zone_home.latitude
        let lon1 = zone_home.longitude
        let lat2 = p.attributes.latitude
        let lon2 = p.attributes.longitude
        distances[p.entity_id] = calculateDistance(lat1, lon1, lat2, lon2)
    } else {
        let wifi = wifis.filter(w => w.linked_person === p.entity_id)
        if (wifi.length > 0) {
            let home_wifis = wifi[0].home_wifis
            let state = wifi[0].state
            if (home_wifis.includes(state)) {
                distances[p.entity_id] = 0
            } else {
                distances[p.entity_id] = (zone_home.radius + 1)
            }
        }
    }
}
global.set("distances", distances)

function calculateDistance(lat1, lon1, lat2, lon2) {
    let R = 6371e3
    lat1 = lat1 * (Math.PI / 180)
    lon1 = lon1 * (Math.PI / 180)
    lat2 = lat2 * (Math.PI / 180)
    lon2 = lon2 * (Math.PI / 180)
    let dlat = lat2 - lat1
    let dlon = lon2 - lon1
    let a = Math.pow(Math.sin(dlat / 2), 2) + Math.cos(lat1) * Math.cos(lat2) * Math.pow(Math.sin(dlon / 2), 2)
    let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a))
    let d = R * c
    return Math.round(d)
}

