function night_brightness_pct_calc() {
    let sun_light_time_dusk = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dusk"].state)
    let sun_light_time_midnight = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_midnight"].state)
    let sun_light_time_dawn = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dawn"].state)
    let sun_light_time_dawn_angle = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dawn_angle"].state)
    let current_time = in_minutes(new Date().toString().slice(16, 21))

    // Pivot
    sun_light_time_rising = sun_light_time_rising + 1440 - sun_light_time_setting
    sun_light_time_dawn_angle = sun_light_time_dawn_angle + 1440 - sun_light_time_setting
    sun_light_time_dawn = sun_light_time_dawn + 1440 - sun_light_time_setting
    sun_light_time_midnight = sun_light_time_dusk < sun_light_time_midnight ? sun_light_time_midnight - sun_light_time_setting : sun_light_time_midnight + 1440 - sun_light_time_setting
    sun_light_time_dusk = sun_light_time_dusk - sun_light_time_setting
    current_time = sun_light_time_setting <= current_time ? current_time - sun_light_time_setting : 1440 - sun_light_time_setting + current_time
    sun_light_time_setting = 0

    // Choix de l'heure de l'aube la plus petite 
    sun_light_time_dawn = sun_light_time_dawn < sun_light_time_dawn_angle ? sun_light_time_dawn : sun_light_time_dawn_angle

    if (sun_light_time_setting <= current_time && current_time < sun_light_time_dusk) {
        return [pct_calc(sun_light_time_setting, sun_light_time_midnight, current_time, false),
        function (light) { return brightness_formula_calc(pct_calc(sun_light_time_setting, sun_light_time_dusk, current_time, false), light) }]
    } else if (sun_light_time_dusk <= current_time && current_time < sun_light_time_midnight - 60) {
        return [pct_calc(sun_light_time_setting, sun_light_time_midnight, current_time, false),
        function (light) { return light.parameters.bright_range[1] }]
    } else if (sun_light_time_midnight - 60 <= current_time && current_time < sun_light_time_midnight) {
        return [pct_calc(sun_light_time_setting, sun_light_time_midnight, current_time, false),
        function (light) { return brightness_formula_calc(pct_calc(sun_light_time_midnight - 60, sun_light_time_midnight, current_time, true), light) }]
    } else if (sun_light_time_midnight <= current_time && current_time < sun_light_time_dawn - 60) {
        return [100, function (light) { return light.auto.night_light ? light.parameters.bright_range[0] : 0 }]
    } else if (sun_light_time_dawn - 60 <= current_time && current_time < sun_light_time_dawn) {
        return [pct_calc(sun_light_time_dawn - 60, sun_light_time_rising, current_time, true),
        function (light) { return brightness_formula_calc(pct_calc(sun_light_time_dawn - 60, sun_light_time_dawn, current_time, true), light) }]
    } else if (sun_light_time_dawn <= current_time && current_time < (sun_light_time_dawn + sun_light_time_rising) / 2) {
        return [pct_calc(sun_light_time_dawn - 60, sun_light_time_rising, current_time, true),
        function (light) { return light.parameters.bright_range[1] }]
    } else if ((sun_light_time_dawn + sun_light_time_rising) / 2 <= current_time && current_time < sun_light_time_rising) {
        return [pct_calc(sun_light_time_dawn - 60, sun_light_time_rising, current_time, true),
        function (light) { return brightness_formula_calc(pct_calc((sun_light_time_dawn + sun_light_time_rising) / 2, sun_light_time_rising, current_time, true), light) }]
    } else {
        return [0, function (light) { return light.parameters.bright_range[0] }]
    }
}

function low_light_brightness_pct_calc() {
    let low_light_compensation = global.get('homeassistant').homeAssistant.states["input_boolean.low_light_compensation"].state == "on"
    if (low_light_compensation) {
        let low_light = parseInt(global.get('homeassistant').homeAssistant.states["input_number.low_light_compensation"].state)
        let illuminances = global.get("illuminances") ?? {}
        let up_to_date = illuminances["exterior"]?.last_changed && (new Date().getTime() - illuminances["exterior"].last_changed) / 1000 < 60 * 60 * 24
        if (up_to_date && illuminances["exterior"]?.["value"] && illuminances["exterior"]["value"] < low_light && sun.elevation > sun_light_angle_setting_rising) {
            return pct_calc(min_low_light_brightness, low_light, illuminances["exterior"]["value"], true)
        } else if (up_to_date && illuminances["exterior"]?.["value"] && illuminances["exterior"]["value"] < low_light && sun.elevation > sun_light_angle_dusk_dawn) {
            return pct_calc(min_low_light_brightness, low_light, illuminances["exterior"]["value"], true) * pct_calc(sun_light_angle_dusk_dawn, sun_light_angle_setting_rising, sun.elevation, false) / 100
        }
    }
    return 0
}

function day_brightness_pct_calc(light) {
    if (sun.elevation > sun_light_angle_setting_rising) {
        return light.parameters.bright_range[1]
    } else if (sun.elevation > sun_light_angle_dusk_dawn) {
        return light.parameters.bright_range[1] * pct_calc(sun_light_angle_dusk_dawn, sun_light_angle_setting_rising, sun.elevation, false) / 100
    } else {
        return 0
    }
}

function brightness_formula_calc(brightness_pct, light) {
    brightness_pct = Math.cos((9 * brightness_pct / 10 - 90) * Math.PI / 180)
    return light.parameters.bright_range[0] + (light.parameters.bright_range[1] - light.parameters.bright_range[0]) * brightness_pct
}

function pct_calc(min, max, nb, reverse) {
    return nb < min ? (reverse ? 100 : 0) : nb > max ? (reverse ? 0 : 100) : reverse ? (max - nb) / Math.abs(min - max) * 100 : (nb - min) / Math.abs(min - max) * 100
}

function in_minutes(timeTxt) {
    timeTxt = timeTxt.split(":")
    return parseInt(timeTxt[0]) * 60 + parseInt(timeTxt[1])
}

function light_action(state, brightness_pct, light) {
    let lp = light.parameters
    let transition = msg.transition !== undefined ? msg.transition : lp.transition_range[state ? 0 : 1]
    if ((light.attributes.supported_color_modes ?? []).find(s => ["hs", "rgb", "xy", "color_temp"].includes(s))) {
        if (state) {
            let type = lp.color == "on" ? "hs_color" : "color_temp_kelvin"
            let value = lp.color == "on" ? [light_color, lp.hs_saturation] : (lp.kelvin_range[1] - lp.kelvin_range[0]) * (100 - light_time) / 100 + lp.kelvin_range[0]
            msgs.push({
                domain: "light", service: "turn_on",
                payload: { data: { entity_id: light.entity_id, transition: transition, brightness_pct: brightness_pct, [type]: value } }
            })
        } else {
            if (light.attributes.flowing !== undefined) {
                let transitions = []
                if (lp.color !== "off") {
                    transitions.push({ "HSVTransition": [light_color, lp.hs_saturation, transition * 1000, lp.bright_range[0]] })
                } else {
                    let color_temp_kelvin = light_time * (light.parameters.kelvin_range[1] - light.parameters.kelvin_range[0]) / 100 + light.parameters.kelvin_range[0]
                    transitions.push({ "TemperatureTransition": [color_temp_kelvin, transition * 1000, lp.bright_range[0]] })
                }
                msgs.push({ domain: "yeelight", service: "start_flow", payload: { data: { entity_id: light.entity_id, action: "off", count: 1, transitions: transitions } } })
            } else {
                msgs.push({ domain: "light", service: "turn_off", payload: { data: { entity_id: light.entity_id, transition: transition } } })
            }
        }
    }
}

function get_color(light_time) {
    let color_rotate = parseInt(global.get('homeassistant').homeAssistant.states["input_number.color_rotate"].state)
    let color_segment = parseInt(global.get('homeassistant').homeAssistant.states["input_number.color_segment"].state)
    let color_reverse = global.get('homeassistant').homeAssistant.states["input_boolean.color_reverse"].state == "on"
    let color = (((360 * (color_segment * light_time / (360 * 100)) + color_rotate) % 360))
    return color_reverse ? 360 - color : color
}

