function update_template(template) {
    if (template.startsWith("raw ")) {
        return add_globals(template) + template.substring(4)
    }
    let ts = template.replace(/\{\{|\}\}/g, "").trim().split(" ")
    let updated_template = []
    for (let i = 0; i < ts.length; i++) {
        updated_template.push((ts[i].includes(`states("${entity_id}")`) ? (isNaN(state) ? `"${state}"` : state) : ts[i]))
    }
    return add_globals(template) + `{{ ${updated_template.join(" ")} }}`
}

function add_globals(template) {
    if (template.includes("global.")) {
        let included_globals = global.keys().filter(g => template.includes("global." + g))
        return "{% set global = {" + included_globals.map(x => (`"${x}": ${JSON.stringify(global.get(x) ?? {})}`)).join(",") + "} %}"
    }
    return ""
}

