            }
        } else if (!isNaN(parseFloat(heat_temp))) {
            return [{ entity_id: entity_id, state: (temp_hum[linked_area]["temperature"]["value"] < parseFloat(heat_temp) ? "on" : "off") }, null]
        }
    }
}

function get_scheduled_temperature(schedule, current_time) {
    let motion_heater = context.get("motion_heater") ?? {}
    let is_motion_class = msg.trigger_class == "motion"
    let schedule_times = Object.keys(schedule)

    if (schedule_times.length > 0) {
        let schedule_time = schedule_times.find(s => s <= current_time)
        let is_on_motion = schedule[schedule_time].includes("motion")
        let match = schedule[schedule_time].match(/-?\d+(\.\d+)?/)
        let target = match ? parseFloat(match[0]) : "no_temp"

        if (target === "no_temp") {
            return { target: "error", status: "no_target", error: schedule[schedule_time] }
        } else {
            if (is_motion_class && is_on_motion) {
                motion_heater[msg.linked_area + "_" + schedule_time] = true
                context.set("motion_heater", motion_heater)
            } else if (!is_on_motion) {
                motion_heater = {}
                context.set("motion_heater", motion_heater)
            }
            return { target: target, status: "" }
        }
    } else {
        return { target: "error", status: "no_schedule" }
    }
}

