function calcRanges(on_time, off_time) {
    let start = time_to_minutes(on_time)
    let end = time_to_minutes(off_time)
    end = start > end ? 1440 + end : end
    let times = { on_time: [], off_time: [] }
    for (let i = 0; i < (parseInt(end - start) / 60); i++) {
        times.on_time.push(minutes_to_time(start + (i + 1) * 60 - waterHeatPct * 60))
        times.off_time.push(minutes_to_time(start + (i + 1) * 60))
    }
    return times
}

function calcIsScheduled(current_time, device) {
    let isTime = false
    if (device.on_time.length == device.off_time.length) {
        if (device.on_time.length == 0) {
            return true
        }
        for (let i = 0; i < device.on_time.length; i++) {
            isTime = isTime || (device.on_time[i] < device.off_time[i] && device.on_time[i] <= current_time && current_time < device.off_time[i])
            isTime = isTime || (device.on_time[i] > device.off_time[i] && (device.on_time[i] <= current_time && current_time <= "23:59" || "00:00" <= current_time && current_time < device.off_time[i]))
            if (isTime) {
                return [device.on_time[i], device.off_time[i]]
            }
        }
    }
    return isTime
}

function time_to_minutes(timeTxt) {
    timeTxt = timeTxt.split(":")
    return parseInt(timeTxt[0]) * 60 + parseInt(timeTxt[1])
}

function minutes_to_time(time) {
    time = time >= 1440 ? time - 1440 : time
    let hours = parseInt(time / 60)
    hours = hours < 10 ? "0" + hours : hours == 0 ? "00" : hours
    let minutes = time % 60
    minutes = minutes < 10 ? "0" + minutes : minutes == 0 ? "00" : minutes
    return hours + ":" + minutes
}

