        if (alarm.getTime() > d.getTime()) {
            next_alarms[p.entity_id] = clean_date(new Date(alarm.setHours(alarm.getHours() - d.getTimezoneOffset() / 60)))
        }
    } else if (typeof p["alarm_clock"] === "object" && regex.test(p["alarm_clock"][d.getDay() + 1])) {
        let a_split = p["alarm_clock"][d.getDay() + 1].split(":")
        let alarm = d
        alarm.setHours(parseInt(a_split[0]))
        alarm.setMinutes(parseInt(a_split[1]))
        alarm.setSeconds(0)
        if (d.getTime() > d.getTime()) {
            next_alarms[p.entity_id] = clean_date(new Date(alarm.setHours(alarm.getHours() - d.getTimezoneOffset() / 60)))
        }
    }
}

