            msgs.push({ delay: (old_state / 100) * time_to_close * 1000 + time_to_aeration * 1000 + time_to_aeration * 1000, entity_id: msg.linked_device, service: "stop_cover" })
        }
    } else {
        let delay = old_state == 0 ? time_to_aeration * 1000 : 0
        if (old_state < new_state) {
            msgs.push({ delay: 0, entity_id: msg.linked_device, service: "open_cover" })
            msgs.push({ delay: delay + ((new_state - old_state) / 100) * time_to_open * 1000, entity_id: msg.linked_device, service: "stop_cover" })
        } else {
            msgs.push({ delay: 0, entity_id: msg.linked_device, service: "close_cover" })
            msgs.push({ delay: delay + ((old_state - new_state) / 100) * time_to_close * 1000, entity_id: msg.linked_device, service: "stop_cover" })
        }
    }
