    let switchDomain = ["input_boolean", "switch", "light"];
    let valueDomain = ["input_number", "input_text"];
    let selectDomain = ["input_select"];
    msg.domain = msg.entity_id.split(".")[0];

    if (switchDomain.includes(msg.domain)) {
        msg.service = "turn_" + msg.state;
        msg.payload = {
            data: {
                entity_id: msg.entity_id
            }
        };
        return msg;
    } else if (selectDomain.includes(msg.domain)) {
        msg.service = "select_option";
        msg.payload = {
            data: {
                entity_id: msg.entity_id, option: msg.state
            }
        };
