let locked_lights = global.get("locked_lights") || [];

if ("lock" in msg && "lock_lights" in msg) {
    if (msg.lock) {
        for (let light of msg.lock_lights) {
            if (!locked_lights.includes(light)) {
                locked_lights.push(light);
            }
        }
    } else if (!msg.lock) {
        for (let light of msg.lock_lights) {
            if (locked_lights.includes(light)) {
                locked_lights.splice(locked_lights.indexOf(light), 1);
            }
        }
    }
    global.set("locked_lights", locked_lights);
    delete msg.lock_lights;
    delete msg.lock;
    return msg;
} else if ("entity_id" in msg) {
    if (!locked_lights.includes(msg.entity_id)) {
        return msg;
    }
}

