    }
}

for (let area of areas) {
    msgs.push({ linkedArea: area, state: area_motions[area] });
}

return [msgs];

if ("lights" in msg && msg.lights.length != 0 && "ambiance_config" in msg && "ambiances_type" in msg.ambiance_config) {
    if (msg.action_type != "stop") {
        let msgs = [];
        for (let light of msg.lights) {
            if ("attributes" in light && "supported_color_modes" in light.attributes && light.attributes.supported_color_modes.includes("rgb")) {
                let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type];
                let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360);
                let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100);
                let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100);
                let delay = 1000 + Math.round(Math.random() * 14000);
                msgs.push({
                    entity_id: light.entity_id,
                    action_type: msg.action_type,
                    ambiance_config: msg.ambiance_config,
                    delay: delay,
                    payload: {
                        data: {
                            entity_id: light.entity_id,
                            hs_color: [hs, saturation],
                            brightness_pct: brightness,
                            transition: Math.round(delay / 100) / 10
                        }
                    }
                });
            }
        }
        msg.reset = true;
        return [msgs, msg];
    } else {
        msg.reset = true;
        return [null, msg];
    }
}

function hsRandomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? highRange - variance : value - variance) : (value + variance > highRange ? lowRange + variance : value + variance);
}

function randomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? lowRange : value - variance) : (value + variance > highRange ? highRange : value + variance);
}

if (msg.action_type != "stop") {
    let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type];
    let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360);
    let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100);
    let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100);
    let delay = 1000 + Math.round(Math.random() * 14000);

    msg.delay = delay;
    if (msg.action_type == "thunderstorm" && delay < 1500) {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
                effect: "Strobe epilepsy!"
            }
        };        
    } else {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
