function getHMS(dateString) {
    let date = dateString == null ? new Date() : new Date(dateString);
    return (date.getHours() < 10 ? "0" + date.getHours() : date.getHours()) + ":" + (date.getMinutes() < 10 ? "0" + date.getMinutes() : date.getMinutes()) + ":00";
