function calcIsHumidity(device) {
    if (Object.keys(roseePts).length == 0) {
        roseePts = calcRoseePts();
        exteriorRoseePt = roseePts["exterior"];
        delete roseePts["exterior"];
    }
    let maxInteriorRoseePtKey = "";
    if (devices.length == 1) {
        maxInteriorRoseePtKey = Object.keys(roseePts).sort(function (a, b) { return roseePts[a] - roseePts[b] }).reverse()[0];
    } else {
        maxInteriorRoseePtKey = roseePts[device.linkedArea];
    }
    let isTempDeviceWork = new Date().getTime() - humidities[maxInteriorRoseePtKey].lastChange < 15 * 60 * 1000;
    let diffRoseePt = exteriorRoseePt - roseePts[maxInteriorRoseePtKey];
    if (!isTempDeviceWork) {
        return ["on", "tempDeviceTimeout"];
    } else if (isTempDeviceWork && humidities[maxInteriorRoseePtKey].tendency > 1 && humidities[maxInteriorRoseePtKey].high) {
        return ["on", "max_humidity"];
    } else if (isTempDeviceWork && Math.abs(diffRoseePt) > rosee_sensibility && !temperatures[maxInteriorRoseePtKey].low) {
        return ["on", "humidity"];
    } else {
        return ["off", ""];
    }
}

function calcRoseePts() {
    let tempKeys = Object.keys(temperatures);
    let roseePts = {};
    for (let key of tempKeys) {
        if (key in temperatures && key in humidities) {
            roseePts[key] = calcRoseePt(temperatures[key].value, humidities[key].value);
        }
    }
    return roseePts;
}

function calcRoseePt(temperature, humidity) {
    let a = (17.27 * temperature) / (237.7 + temperature);
    return (237.7 * (a + Math.log(humidity / 100))) / (17.27 - (a + Math.log(humidity / 100)));
}

