    } else {
        msg.brightnessPct = 100 * Math.cos((18 * msg.currentTimeStatus / 10 - 90) * Math.PI / 180);
    }
    msg.brightnessPct = msg.brightnessPct * msg.brightRange[1] / 100 + msg.brightRange[0] * (100 - msg.brightnessPct) / 100;
}

