let linked_device = msg.linked_device
let entity_id = msg.entity_id
let new_state = msg.new_state
let old_state = msg.old_state
let remote = msg.remote
let msgs = []

if ("increase" in remote && "decrease" in remote) {
    let diff = new_state - old_state
    setStateMsgs({ "num_repeats": Math.abs(diff), "command": (diff < 0 ? remote["decrease"] : remote["increase"]) })
    return [msgs, null]
} else if (new_state in remote) {
    setStateMsgs(remote[new_state])
    return [msgs, "off" in remote ? null : { "state": "off" }]
}

function setStateMsgs(remote_state) {
    if ("command" in remote_state && remote_state.command.startsWith("b64:")){
        let num_repeats = "num_repeats" in remote_state ? remote_state.num_repeats : 0
        let delay_secs = "delay_secs" in remote_state ? remote_state.delay_secs : 0
        msgs.push({
            payload: {
                data: {
                    entity_id: linked_device,
                    command: remote_state.command,
                    num_repeats: num_repeats,
                    delay_secs: delay_secs
                }
            }
        })
    }
}

let count_check = msg.light_effect?.count ?? 0
let rgbs_check = msg.light_effect?.rgbs ?? []

if (
    count_check > 0 && rgbs_check.length == 2
    && Array.isArray(rgbs_check[0]) && rgbs_check[0].length == 3
    && Array.isArray(rgbs_check[1]) && rgbs_check[1].length == 3
) {

    let motion_areas = global.get("motion_areas") || {}
    let msgs = []

    Object.entries(motion_areas).forEach(([key, value]) => {
        if (value != "off") {
            msgs.push({ linked_area: key, light_effect: msg.light_effect })
        }
    })

    return [msgs]
}

let light_control = global.get("light_control") ?? {}
let cycle_delay = 1000
let transition = 250
let msgs = []

for (let light of msg.lights) {
    let supported_color_modes = light?.attributes?.supported_color_modes ?? []
    let color_modes = ["hs", "rgb", "xy"]
    let support_color = (color_modes.find(a => supported_color_modes.includes(a)) ?? false)

    if (support_color && ((!(light_control["locked"].includes(light.entity_id))) || light.state !== "off")) {
        let message = {}

        for (let i = 0, j = msg.light_effect.count * 2 - 1; i < j; i++) {
            message = {
                light: light.entity_id,
                service: "on",
                payload: { data: { brightness: Math.max(...msg.light_effect.rgbs[i % 2]), transition: (transition / 1000) } },
                delay: i * (cycle_delay - transition)
            }

            if (color_modes) {
                message["payload"]["data"]["rgb_color"] = msg.light_effect.rgbs[i % 2]
            }
            msgs.push({ ...message })
        }

        message = {
            light: light.entity_id,
            service: light.state,
            payload: { data: { transition: (transition / 1000) } },
            delay: (msg.light_effect.count * 2 - 1) * (cycle_delay - transition)
        }

        if (light.state == "on") {
            message["payload"]["data"]["brightness"] = light.attributes.brightness
            if (light.attributes.color_mode == "color_temp") {
                message["payload"]["data"]["color_temp"] = light.attributes.color_temp
            } else {
                message["payload"]["data"]["rgb_color"] = light.attributes.rgb_color
            }
        }
        msgs.push({ ...message })
    }
}

return [msgs]

let persons = msg.persons
let msgs = []

if (msg.error === true) {
    persons = msg.persons.filter(p => (p.notify_mobile_app_on_errors ?? false))
} else if (Array.isArray(msg.send_to)) {
    persons = msg.persons.filter(p => msg.send_to.includes(p.name))
}

for (let p of persons.filter(p => p.mobile_app_name !== "")) {
    let notification = { service: p.mobile_app_name, payload: { data: { message: msg.clear_on_phone ? "clear_notification" : (msg.message ?? ""), data: {} } } }

    if (msg.title) {
        notification.payload.data.title = msg.title
    }

    if (msg.data) {
        notification.payload.data["data"] = msg.data
    }

    notification.payload.data["data"]["tag"] = msg.notification_id
    msgs.push(notification)
}

return [msgs]

if (msg.send_mobile === true || msg.clear_on_phone === true) {
    msg.search = { "is": { linked_class: "person" } }
    msg.output_name = "persons"
    msg.output_empty_results = false
    return msg
}

let speaker_day_night_mode = global.get("homeassistant").homeAssistant.states["input_boolean.speaker_day_night_mode"].state == "on"
let linked_area = msg.linked_area
let audio_type = msg.audio_type
let speakers = msg.speakers
let msgs = []

if (speakers.length == 1) {
    set_msgs(speakers[0].entity_id, audio_type, msg[audio_type])
} else {
    for (let speaker of speakers.filter(s => s.linked_area == (linked_area ?? s.linked_area))) {
        set_msgs(speaker.entity_id, audio_type, msg[audio_type])
    }
}
return [msgs]

function set_msgs(entity_id, audio_type, content) {

    let home_configuration = global.get("home_configuration") ?? {}
    let tts_voice = home_configuration.tts_voice ?? ""
    let message = {
        entity_id: entity_id,
        volume_level: msg.volume_level,
        rate: (audio_type === "speak" ? 125 * content.length : 10 * 1000)
    }
    if (msg.light_effect?.count && msg.light_effect?.rgbs) {
        message.light_effect = {
            count: msg.light_effect.count,
            rgbs: msg.light_effect.rgbs
        }
    }

    if (audio_type === "speak" && tts_voice !== "") {
        msgs.push({
            ...message,
            domain: "tts",
            service: tts_voice,
            payload: {
                data: {
                    entity_id: entity_id,
                    message: content
                }
            }
        })
    } else if (audio_type === "sound") {
        msgs.push({
            ...message,
            domain: "media_player",
            service: "play_media",
            payload: {
                data: {
                    entity_id: entity_id,
                    media_content_id: "media-source://media_source/media/" + content,
                    media_content_type: "music"
                }
            }
        })
    } else if (audio_type === "link") {
        msgs.push({
            ...message,
            domain: "media_player",
            service: "play_media",
            payload: {
                data: {
                    entity_id: entity_id,
                    media_content_id: content,
                    media_content_type: "music"
                }
            }
        })
    }
}

let audio_type = Object.keys(msg).find(a => ["speak", "sound", "link"].includes(a))
if (audio_type) {
    return {
        audio_type: audio_type,
        [audio_type]: msg[audio_type],
        volume_level: volume_level_calc(),
        light_effect: {
            count: msg.light_effect?.count ?? 0,
            rgbs: msg.light_effect?.rgbs ?? []
        },
        search: { "is": { linked_class: "speaker" }, "not": { state: "unavailable" } },
        output_name: "speakers",
        output_empty_results: false
    }
}

function volume_level_calc() {
    if (msg.volume_level) {
        return msg.volume_level
    } else {
        if (global.get("homeassistant").homeAssistant.states["input_boolean.speaker_day_night_mode"].state == "on") {
            return parseInt(global.get("homeassistant").homeAssistant.states["input_number.speaker_day_mode_volume"].state) / 100
        } else {
            return parseInt(global.get("homeassistant").homeAssistant.states["input_number.speaker_night_mode_volume"].state) / 100
        }
    }
}

if (msg.entity_id) {
    let switch_domain = ["input_boolean", "switch", "light"]
    let value_domain = ["input_number", "input_text"]
    let select_domain = ["input_select", "select"]
    msg.domain = msg.entity_id.split(".")[0]

    if (switch_domain.includes(msg.domain) && ["on", "off", "toggle"].includes(msg.state)) {
        msg.service = msg.state === "toggle" ? msg.state : "turn_" + msg.state
        msg.payload = {
            data: {
                entity_id: msg.entity_id
            }
        }
        return [msg, null]
    } else if (select_domain.includes(msg.domain) && msg.state) {
        msg.service = "select_option"
        msg.payload = {
            data: {
                entity_id: msg.entity_id, option: msg.state
            }
        }
        return [msg, null]
    } else if (value_domain.includes(msg.domain) && (msg.domain == "input_number" && !isNaN(msg.state) || msg.domain == "input_text")) {
        msg.service = "set_value"
        msg.payload = {
            data: {
                entity_id: msg.entity_id, value: msg.state
            }
        }
        return [msg, null]
    } else if (msg.domain === "input_datetime" && msg.state) {
        let type =
            msg.state.search(/[0-9]{4}-[0-9]{2}-[0-9]{2} [0-9]{2}:[0-9]{2}/) == 0 ? "datetime"
                : msg.state.search(/[0-9]{4}-[0-9]{2}-[0-9]{2}/) == 0 ? "date"
                    : msg.state.search(/[0-9]{2}:[0-9]{2}/) == 0 ? "time" : ""
        if (type) {
            msg.service = "set_datetime"
            msg.payload = {
                data: {
                    entity_id: msg.entity_id, [type]: msg.state
                }
            }
            return [msg, null]
        }
    } else if (msg.domain === "action") {
        return [{ ...msg.state }, null]
    } else if (msg.domain === "emulate") {
        msg.payload = {
            event: "emulate",
            data: {
                entity_id: msg.entity_id.replace(msg.domain + ".", ""),
                ...msg.state
            }
        }
        return [null, msg]
    }
}

if (msg.error?.message) {
    let notAllowedError = msg.blockedErrors.find(e => (msg.error.message.includes(e)))
    if (!notAllowedError) {
        let notifications = global.get("notifications") ?? {}

        msg.notification_id = "error_" + msg.flowTitle + "_" + (msg.error?.source.id ?? new Date().getTime())
        msg.title = "Erreur détéctée"
        msg.message = "Erreur : " + msg.flowTitle + " > " + msg.error?.source?.name + " > " + msg.error?.message
        msg.alert_type = "error"

        if (notifications[msg.notification_id]) {
            msg.message += ", apparue " + (notifications[msg.notification_id].count + 1) + " fois."
        } else {
            msg.send_mobile = true
        }
        msg.error = true
        return msg
    }
}

if (msg.error?.source?.name) {
    let allowedError = msg.blockedErrors.filter(error => msg.error.message.includes(error)).length == 0
    if (allowedError) {
        msg.payload = { time: msg.currentTime, ...msg }
        return msg
    }
}

