let home_configuration = global.get("home_configuration") ?? {}
let entities = global.get("homeassistant").homeAssistant.states
let found = []

for (let entity_id of Object.keys(entities)) {
    let entity = entities[entity_id]
    if (home_configuration[entity_id]) {
        entity = { ...entity, ...home_configuration[entity_id] }
    }
    let entity_search = { ...entity, ...entity.attributes }
    if (search(entity_search)) {
        entity.state = isNaN(entity.state) ? entity.state : parseFloat(entity.state)
        found.push(entity)
    }
}

let output_empty_results = (msg.output_empty_results ?? false) || found.length > 0
if (output_empty_results) {
    msg[msg.output_name] = found
    delete msg.output_empty_results
    delete msg.output_name
    delete msg.search
    return msg
}

function search(entity) {
    if (msg.search["is"]) {
        for (let search_key of Object.keys(msg.search["is"])) {
            if (Array.isArray(msg.search["is"][search_key])) {
                if (!(msg.search["is"][search_key].includes(entity[search_key]))) {
                    return false
                }
            } else if (msg.search["is"][search_key] === undefined || !(entity[search_key] == msg.search["is"][search_key])) {
                return false
            }
        }
    }
    if (msg.search["not"]) {
        for (let search_key of Object.keys(msg.search["not"])) {
            if (Array.isArray(msg.search["not"][search_key])) {
                if (msg.search["not"][search_key].includes(entity[search_key])) {
                    return false
                }
            } else if (msg.search["not"][search_key] === undefined || entity[search_key] == msg.search["not"][search_key]) {
                return false
            }
        }
    }
    return true
}

