function get_state(entity_id) {
    if (entity_id in global.get("homeassistant").homeAssistant.states) {
        return global.get("homeassistant").homeAssistant.states[entity_id].state
    } else {
        return ""
    }
}

function date_to_display(notif_creation_date) {
    let month_trad_fr = {
        1: "Janvier",
        2: "Février",
        3: "Mars",
        4: "Avril",
        5: "Mai",
        6: "Juin",
        7: "Juillet",
        8: "Août",
        9: "Septembre",
        10: "Octobre",
        11: "Novembre",
        12: "Décembre"
    }

    let notifCreationDateSplit = notif_creation_date.split(" ")
    let notifMonth = parseInt(notifCreationDateSplit[0])
    let notifDay = parseInt(notifCreationDateSplit[1])
    let notifHour = notifCreationDateSplit[2]

    if (current_day == notifDay && current_month == notifMonth) {
        return notifHour + " : "
    } else if (current_day - notifDay == 1 && current_month == notifMonth) {
        return "Hier à " + notifHour + " : "
    } else if (current_day - notifDay == 2 && current_month == notifMonth) {
        return "Avant-hier à " + notifHour + " : "
    } else {
        return "Le " + notifDay + " " + month_trad_fr[notifMonth] + " à " + notifHour + " : "
    }
}

