let average = JSON.parse(global.get("homeassistant").homeAssistant.states["input_text.json_exterior_average_temp"].state)
let temp_hum = global.get("temp_hum") ?? {}
msg.entity_id = "input_text.json_exterior_average_temp"

if (
    temp_hum["exterior"]?.["temperature"]?.value &&
    temp_hum["exterior"]?.["temperature"]?.last_changed &&
    ((new Date().getTime() - temp_hum["exterior"]?.["temperature"]?.last_changed) / 1000) < 60 * 60
) {
    if (!Array.isArray(average)) {
        average = [temp_hum["exterior"]["temperature"].value, 1]
    } else if (Array.isArray(average)) {
        if (average[1] < 10) {
            average[0] = calculateAverage(average)
            average[1]++
        } else {
            average[0] = calculateAverage(average)
        }
    }
    msg.state = JSON.stringify(average)
    return msg
}

function calculateAverage(average) {
    return Math.round(((average[0] * average[1] + temp_hum["exterior"]["temperature"].value) / (average[1] + 1)) * 100) / 100
}

