            msgs.push({ service: "turn_on", payload: { data: { entity_id: light_support_color, hs_color: x } } })
            lock_lights()
        }
        // saturation_step_up / saturation_step_down
        else if (action.includes("saturation_step_") && light_control["controller"][controller].hs_color_step) {
            let x = light_control["controller"][controller].hs_color_step
            x[1] = action.includes("_down") ? (x[1] - 25 < 0 ? (action.includes("_loop") ? 100 : 0) : x[1] - 25) : (x[1] + 25 > 100 ? (action.includes("_loop") ? 0 : 100) : x[1] + 25)
            light_control["controller"][controller].hs_color_step = x
            light_control["controller"][controller].action = "on"
            let light_support_color = msg.lights.filter(l => l.attributes.supported_color_modes.find(c => ["xy", "hs", "rgb"].includes(c))).map(l => l.entity_id)
            msgs.push({ service: "turn_on", payload: { data: { entity_id: light_support_color, hs_color: x } } })
            lock_lights()
        }
    }
    global.set("light_control", light_control)

    if (action === "auto") {
        return [null, { sound: "light_auto_off.mp3", linked_area: msg.linked_area, state: "on" }]
    } else if (msgs.length > 0) {
        return [msgs, null]
    }
}

function formula(range, number) {
    let step = (range[1] - range[0]) / 4
    return range[0] + step * (number - 1)
}

