function turn_on(brightnessPct, currentTimeStatus, light) {
    let action = { domain: "light", service: "turn_on", payload: { data: { entity_id: light.entity_id, brightness_pct: brightnessPct, transition: light.parameters.transition_range[0] } } }
    if (light.parameters.color == "on") {
        let low_light_compensation_hs_color = [30, 50]
        action.payload.data["hs_color"] = [
            get_color(currentTimeStatus) * (100 - low_light_brightnessPct) / 100 + low_light_compensation_hs_color[0] * low_light_brightnessPct / 100,
            light.parameters.hs_saturation * (100 - low_light_brightnessPct * low_light_compensation_hs_color[1] / 100) / 100
        ]
    } else {
        let color_temp = currentTimeStatus * (light.parameters.color_temp_range[1] - light.parameters.color_temp_range[0]) / 100 + light.parameters.color_temp_range[0]
        action.payload.data["color_temp"] = color_temp
    }
    return action
}

function turn_off(currentTimeStatus, light) {
    let action = {}
    if (light.parameters.color != "off" && light.attributes?.flowing !== undefined) {
        action = {
            domain: "yeelight", service: "start_flow", payload: {
                data: {
                    entity_id: light.entity_id, action: "off", count: 1, transitions: [
                        { "HSVTransition": [get_color(currentTimeStatus), light.parameters.hs_saturation, light.parameters.transition_range[1] * 1000, light.parameters.bright_range[0]] }
                    ]
                }
            }
        }
    } else {
        action = {
            domain: "light", service: "turn_off", payload: {
                data: {
                    entity_id: light.entity_id,
                    transition: light.parameters.transition_range[1]
                }
            }
        }
    }
    return action
}

function get_color(currentTimeStatus) {
    let color_rotate = parseInt(global.get('homeassistant').homeAssistant.states["input_number.color_rotate"].state)
    let color_segment = parseInt(global.get('homeassistant').homeAssistant.states["input_number.color_segment"].state)
    let color_reverse = global.get('homeassistant').homeAssistant.states["input_boolean.color_reverse"].state == "on"
    let color = (((360 * (color_segment * currentTimeStatus / (360 * 100)) + color_rotate) % 360))
    return color_reverse ? 360 - color : color
}

