            let waits = global.get("waits") || {}
            waits[a[1]] = { action: a[2] }
            global.set("waits", waits)
            waits_msgs.push({ wait_key: a[1], delay: parseFloat(a[0].split(" ")[1]) * 1000 })
        } else if (a[0] in global.get("homeassistant").homeAssistant.states
            && global.get("homeassistant").homeAssistant.states[a[0]].state != a[1]
            || a[0].startsWith("emulate.") || a[0] === "action" || a[0] === "notification"
        ) {
            action_msgs.push({
                entity_id: a[0],
                state: a[1],
                delay: a.length == 3 ? a[2] * 1000 : 0
            })
        }
    }
    return [waits_msgs, action_msgs]
}

