function calcIsScheduled(currentTime, device) {
    if (device.on_time.length === device.off_time.length) {
        let isTime = false
        for (let i = 0; i < device.on_time.length; i++) {
            isTime = isTime
                || (device.on_time[i] < device.off_time[i] && device.on_time[i] <= currentTime && currentTime < device.off_time[i])
                || (device.on_time[i] > device.off_time[i] && (
                    device.on_time[i] <= currentTime && currentTime <= "23:59"
                    || "00:00" <= currentTime && currentTime < device.off_time[i]))

            if (isTime) {
                return ["on", "scheduled"]
            }
        }
    }
    return ["off", ""]
}

