    ) {
        let alarm = new Date(global.get("homeassistant").homeAssistant.states[persons[i]["alarm_clock"]].state)
        alarm = new Date(alarm.setHours(alarm.getHours() - d.getTimezoneOffset() / 60))
        if (alarm.getTime() > d.getTime()) {
            next_alarms[persons[i].entity_id] = clean_date(alarm)
        }
    } else if (
        typeof persons[i]["alarm_clock"] === "object" &&
        persons[i]["alarm_clock"][d.getDay() + 1] &&
        regex.test(persons[i]["alarm_clock"][d.getDay() + 1])
    ) {
        let month = (d.getMonth() + 1) < 10 ? "0" + (d.getMonth() + 1) : (d.getMonth() + 1)
        let day = d.getDate() < 10 ? "0" + d.getDate() : d.getDate()
        let alarm = new Date(d.getFullYear() + "-" + month + "-" + day + " " + persons[i]["alarm_clock"][d.getDay() + 1])
        alarm = new Date(alarm.setHours(alarm.getHours() - d.getTimezoneOffset() / 60))
        if (alarm.getTime() > d.getTime()) {
            next_alarms[persons[i].entity_id] = clean_date(alarm)
        }
    }
}

